    creatureFactory.createBait(bird, bait.bait.BREAD, bait.getBread())
    creatureFactory.createBait(bird, bait.bait.BREAD_SLICES, bait.getBreadSlices())
    creatureFactory.createBait(bird, bait.bait.CEREAL, bait.getCereal())
